values:
Algorithm for arbitrary long (well up to (2^31,31)) Hamming Codes.  One should never code after midnight.
<LJ-CUT>
a=new Array;
size=2048;
// run test for q times
for (q=1;q<=1;q++)
{
// create random test data
for (i=1;i<=size-1;i++)
 {
 a[i]=Math.round(Math.random())
 }
// zero check bits
for (k=1;k<=size-1;k=k*2)
{
 a[k]=0;
}
// print sample data in r1 (with zeroed check data)
r1=""
for (i=size-1;i>=1;i--)
 r1=r1+""+a[i]+""
// compute checksum 
for (i=1;i<=size-1;i++)
    for (k=1;k<=size-1;k=k*2)
    {
      if ((i!=k) && (i&k))
         {
          a[k]=a[k]^a[i];
         }
    }

// pick a random bit to flip
j=Math.round(Math.random()*(size-2))+1
a[j]=1-a[j];
r2=""
// show corrupted data
for (i=size-1;i>=1;i--)
 r2=r2+""+a[i]+""

// compute checksum
for (i=1;i<=size-1;i++)
    for (k=1;k<=size-1;k=k*2)
    {
      if ((i!=k) && (i&k))
         {
          a[k]=a[k]^a[i];
         }
    }

// which bit in binary is flipped from computed checkbits
r3=""
for (k=1;k<=size-1;k=k*2)
 {
  r3=a[k]+r3;
 }

//fix that bit
j=0;
for (k=1;k<=size-1;k=k*2)
 {
  j=j+(a[k]*k);
 }
// show which bit I'm going to flip (in decimal)
r3=r3+"  "+j
// flip that bit
a[j]=1-a[j]
// erase the computed checksum
for (k=1;k<=size-1;k=k*2)
{
 a[k]=0;
}
// print result, r1==r4 if algorithm worked
r4=""
for (i=size-1;i>=1;i--)
 r4=r4+""+a[i]+""

// show if algorithm failed
if (r1!=r4) { r4=r4+"   "+q; q=10003;}
}
