/*icon for locked entries in sidebar*/
#summary img[src="http://stat.livejournal.com/img/icon_protected.gif"]{
width: 0;
height: 0;
padding: 16px 16px 0px 0px;
background: url(http://img526.imageshack.us/img526/7529/lockedbn8.png) !important;
}

/*icon for private entries in sidebar*/
#summary img[src="http://stat.livejournal.com/img/icon_private.gif"]{
width: 0;
height: 0;
padding: 16px 16px 0px 0px;
background: url(http://img512.imageshack.us/img512/5745/privaterg0.png) !important;
}

/*icon for user info in sidebar*/
#profile img[src="http://stat.livejournal.com/img/userinfo.gif"]{
width: 0;
height: 0;
padding: 16px 16px 0px 0px;
background: url(http://img520.imageshack.us/img520/2941/ljuserzq9.png) !important;
}
</textarea>

